mapper.select:
SELECT ID AS id, URL AS url, SHARE_ID AS shareId
FROM T_PICTURE
WHERE SHARE_ID = #{shareId};

mapper.update:
UPDATE T_SHARE
SET CONTENT = #{content}
WHERE ID = #{id}

UPDATE T_SHARE
SET LIKENUM = LIKENUM+1
WHERE ID = #{id};

UPDATE T_SHARE
SET LIKENUM = LIKENUM-1
WHERE ID = #{id};

UPDATE T_SHARE
SET COLLECTNUM = COLLECTNUM+1
WHERE ID = #{id};

UPDATE T_SHARE
SET COMMENTNUM = COMMENTNUM+1
WHERE ID = #{id};

mapper.select:
SELECT COUNT(*)
FROM T_SHARE AS S,T_USER AS U,T_USER_INFO AS I
WHERE S.USER_ID=U.ID AND U.ID=I.USER_ID AND S.STATE='public' AND U.LOCKED=0;

SELECT COUNT(*)
FROM T_SHARE AS S,T_USER AS U,T_USER_INFO AS I
WHERE S.USER_ID=U.ID AND U.ID=I.USER_ID AND S.USER_ID=#{userId} AND U.LOCKED=0;
